import React, { useEffect, useRef } from 'react';
import { useGameLoop } from '../hooks/useGameLoop';
import { handleInput } from '../core/inputController';

export default class Player {
  constructor(args) {
    this.position = args.position;
    this.velocity = { x: 0, y: 0 };
    this.rotation = 0;
    this.rotationSpeed = 6;
    this.speed = 0.15;
    this.inertia = 0.99;
    this.radius = 20;
  }

  destroy() {
    this.delete = true;
  }

  rotate(dir) {
    if (dir === 'LEFT') {
      this.rotation -= this.rotationSpeed;
    }
    if (dir === 'RIGHT') {
      this.rotation += this.rotationSpeed;
    }
  }

  accelerate() {
    this.velocity.x -= Math.sin(-this.rotation * Math.PI / 180) * this.speed;
    this.velocity.y -= Math.cos(-this.rotation * Math.PI / 180) * this.speed;
  }

  decelerate() {
    this.velocity.x += Math.sin(-this.rotation * Math.PI / 180) * (this.speed / 2);
    this.velocity.y += Math.cos(-this.rotation * Math.PI / 180) * (this.speed / 2);
  }

  update(keys, width, height) {
    if (keys.a) {
      this.rotate('LEFT');
    }
    if (keys.d) {
      this.rotate('RIGHT');
    }
    if (keys.w) {
      this.accelerate();
    }
    if (keys.s) {
      this.decelerate();
    }

    // Apply inertia
    this.velocity.x *= this.inertia;
    this.velocity.y *= this.inertia;

    // Update position
    this.position.x += this.velocity.x;
    this.position.y += this.velocity.y;

    // Screen wrap
    if (this.position.x > width + this.radius) {
      this.position.x = -this.radius;
    } else if (this.position.x < -this.radius) {
      this.position.x = width + this.radius;
    }
    if (this.position.y > height + this.radius) {
      this.position.y = -this.radius;
    } else if (this.position.y < -this.radius) {
      this.position.y = height + this.radius;
    }
  }

  draw(context) {
    context.save();
    context.translate(this.position.x, this.position.y);
    context.rotate(this.rotation * Math.PI / 180);
    context.strokeStyle = '#ffffff';
    context.fillStyle = '#000000';
    context.lineWidth = 2;
    context.beginPath();
    context.moveTo(0, -this.radius);
    context.lineTo(this.radius / 2, this.radius / 2);
    context.lineTo(-this.radius / 2, this.radius / 2);
    context.closePath();
    context.fill();
    context.stroke();
    context.restore();
  }
}
