import React from 'react';

export default class Asteroid {
  constructor(args) {
    this.position = args.position;
    this.velocity = {
      x: Math.random() * 2 - 1,
      y: Math.random() * 2 - 1,
    };
    this.rotation = 0;
    this.rotationSpeed = Math.random() * 0.5 - 0.25;
    this.radius = args.size || 50;
    this.hitPoints = Math.floor(this.radius / 12) + 1; // ~5 hits for default size
    this.delete = false;

    // Generate a misshapen polygon shape
    this.shape = [];
    const sides = Math.floor(Math.random() * 5) + 7; // 7 to 11 sides
    for (let i = 0; i < sides; i++) {
      const angle = (i / sides) * 2 * Math.PI;
      const distance = this.radius * (0.8 + Math.random() * 0.4);
      this.shape.push({
        x: Math.cos(angle) * distance,
        y: Math.sin(angle) * distance,
      });
    }
  }

  destroy() {
    this.delete = true;
  }

  hit() {
    this.hitPoints--;
    if (this.hitPoints <= 0) {
      this.destroy();
    }
  }

  update(width, height) {
    this.position.x += this.velocity.x;
    this.position.y += this.velocity.y;
    this.rotation += this.rotationSpeed;

    // Screen wrap
    if (this.position.x > width + this.radius) {
      this.position.x = -this.radius;
    } else if (this.position.x < -this.radius) {
      this.position.x = width + this.radius;
    }
    if (this.position.y > height + this.radius) {
      this.position.y = -this.radius;
    } else if (this.position.y < -this.radius) {
      this.position.y = height + this.radius;
    }
  }

  draw(context) {
    context.save();
    context.translate(this.position.x, this.position.y);
    context.rotate(this.rotation * Math.PI / 180);
    context.strokeStyle = '#ffffff';
    context.lineWidth = 2;
    context.beginPath();
    context.moveTo(this.shape[0].x, this.shape[0].y);
    for (let i = 1; i < this.shape.length; i++) {
      context.lineTo(this.shape[i].x, this.shape[i].y);
    }
    context.closePath();
    context.stroke();
    context.restore();
  }
}
