import React, { useEffect, useRef } from 'react';
import { useGameLoop } from '../hooks/useGameLoop';
import { handleInput } from '../core/inputController';

const Player = ({ position, setPosition, shoot }) => {
    const playerRef = useRef();

    const movePlayer = (delta) => {
        const newPosition = { ...position };

        if (handleInput('W')) newPosition.y -= 1 * delta;
        if (handleInput('S')) newPosition.y += 1 * delta;
        if (handleInput('A')) newPosition.x -= 1 * delta;
        if (handleInput('D')) newPosition.x += 1 * delta;

        setPosition(newPosition);
    };

    useGameLoop(movePlayer);

    const handleShoot = (event) => {
        if (event.code === 'Space') {
            shoot();
        }
    };

    useEffect(() => {
        window.addEventListener('keydown', handleShoot);
        return () => {
            window.removeEventListener('keydown', handleShoot);
        };
    }, []);

    return (
        <div
            ref={playerRef}
            style={{
                position: 'absolute',
                left: position.x,
                top: position.y,
                width: '50px',
                height: '50px',
                backgroundColor: 'blue',
                borderRadius: '50%',
            }}
        />
    );
};

export default Player;
