import React, { useEffect, useRef } from 'react';
import { handleInput } from '../core/inputController';

const Game = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    let animationFrameId;

    const gameLoop = () => {
      const keys = handleInput();
      // console.log(keys); // You can uncomment this to test keyboard input in the browser console

      context.fillStyle = 'black';
      context.fillRect(0, 0, canvas.width, canvas.height);

      // Future game logic will go here

      animationFrameId = window.requestAnimationFrame(gameLoop);
    };

    gameLoop();

    return () => {
      window.cancelAnimationFrame(animationFrameId);
      // You might want to remove event listeners here in a more complex app
    };
  }, []);

  return <canvas ref={canvasRef} />;
};

export default Game;
