import React from 'react';

const Bullet = ({ position, direction }) => {
    const bulletStyle = {
        position: 'absolute',
        left: position.x,
        top: position.y,
        width: '5px',
        height: '5px',
        backgroundColor: 'yellow',
        borderRadius: '50%',
        transform: `translate(-50%, -50%) rotate(${Math.atan2(direction.y, direction.x)}rad)`,
    };

    return <div style={bulletStyle} />;
};

export default Bullet;
