import React, { useEffect, useRef, useState } from 'react';
import { useGameLoop } from '../hooks/useGameLoop';
import Player from './Player';
import Asteroid from './Asteroid';
import Bullet from './Bullet';
import { handleInput } from '../core/inputController';
import { updateGameState } from '../core/gameEngine';

const Game = () => {
    const [player, setPlayer] = useState({ x: 400, y: 300, bullets: [] });
    const [asteroids, setAsteroids] = useState([]);
    const canvasRef = useRef(null);

    const spawnAsteroids = () => {
        const newAsteroids = Array.from({ length: 5 }, () => ({
            x: Math.random() * window.innerWidth,
            y: Math.random() * window.innerHeight,
            size: Math.random() * 30 + 20,
        }));
        setAsteroids(newAsteroids);
    };

    useEffect(() => {
        spawnAsteroids();
    }, []);

    const gameLoop = () => {
        handleInput(player, setPlayer);
        updateGameState(player, asteroids, setAsteroids);
        draw();
    };

    const draw = () => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        context.clearRect(0, 0, canvas.width, canvas.height);

        // Draw player
        context.fillStyle = 'blue';
        context.fillRect(player.x, player.y, 20, 20);

        // Draw asteroids
        asteroids.forEach(asteroid => {
            context.fillStyle = 'gray';
            context.beginPath();
            context.arc(asteroid.x, asteroid.y, asteroid.size, 0, Math.PI * 2);
            context.fill();
        });

        // Draw bullets
        player.bullets.forEach(bullet => {
            context.fillStyle = 'red';
            context.fillRect(bullet.x, bullet.y, 5, 5);
        });
    };

    useGameLoop(gameLoop);

    return (
        <canvas ref={canvasRef} width={800} height={600} />
    );
};

export default Game;
