export const InputController = (() => {
    const keys = {
        W: false,
        A: false,
        S: false,
        D: false,
        SPACE: false,
    };

    const handleKeyDown = (event) => {
        switch (event.code) {
            case 'KeyW':
                keys.W = true;
                break;
            case 'KeyA':
                keys.A = true;
                break;
            case 'KeyS':
                keys.S = true;
                break;
            case 'KeyD':
                keys.D = true;
                break;
            case 'Space':
                keys.SPACE = true;
                break;
        }
    };

    const handleKeyUp = (event) => {
        switch (event.code) {
            case 'KeyW':
                keys.W = false;
                break;
            case 'KeyA':
                keys.A = false;
                break;
            case 'KeyS':
                keys.S = false;
                break;
            case 'KeyD':
                keys.D = false;
                break;
            case 'Space':
                keys.SPACE = false;
                break;
        }
    };

    const getKeys = () => {
        return keys;
    };

    const init = () => {
        window.addEventListener('keydown', handleKeyDown);
        window.addEventListener('keyup', handleKeyUp);
    };

    const destroy = () => {
        window.removeEventListener('keydown', handleKeyDown);
        window.removeEventListener('keyup', handleKeyUp);
    };

    return {
        init,
        destroy,
        getKeys,
    };
})();
